import React, { ReactNode } from "react";
import {Row, Col, Container} from "react-bootstrap";
import enlargeIcon from './enlargeIcon.png'
import ethereumIcon from './ethereumIcon.png'
import styled from 'styled-components'
import { Key, ReactChild, ReactFragment, ReactPortal, useCallback, useEffect, useMemo, useState } from 'react';
import axios from 'axios';
import { useWallet } from '@solana/wallet-adapter-react';
import { useQuery, gql } from "@apollo/client";

interface Props {
    children?: ReactNode
    // any props that come into the component
    wallet: any
}

//icy tools trending collections on eth query
const  TRENDING_COLLECTIONS_QUERY = gql`
query TrendingCollections {
  contracts(orderBy: SALES, orderDirection: DESC) {
    edges {
      node {
        address
        ... on ERC721Contract {
          name
          stats {
            totalSales
            average
            ceiling
            floor
            volume
          }
          symbol
        }
      }
    }
  }
}`;

//icy tools trending collections on eth query

const USER_COLLECTIONS = gql`
query MEVTokens {
  wallet(ensName: "mevcollector.eth") {
    tokens {
      edges {
        node {
          tokenId
          contract {
            ... on ERC721Contract {
              symbol
              name
            }
          }
        }
      }
    }
  }
}
`;

export const CollectionsTable = ({ children, ...props }: Props) => {

  const [collections, setCollections] = useState<string>();
  const [data, setData] = useState<any>();
  const wallet = props.wallet;

  const getCollections = async () => {

    await fetch("api-devnet.magiceden.dev/v2/launchpad/collections?offset=0&limit=200")
    .then(response => response.text())
    .then(result => setCollections(result))
    .catch(error => console.log('error', error));


    // const response = await fetch("api-devnet.magiceden.dev/v2/launchpad/collections?offset=0&limit=200");
    // let data = await response.text();
    // setCollections(data);

    // var config = {
    //   method: 'get',
    //   url: 'api-devnet.magiceden.dev/v2/launchpad/collections?offset=0&limit=200'
    // };

    // axios(config)
    // .then(function (response) {
    //   console.log(JSON.stringify(response.data));
    // })
    // .catch(function (error) {
    //   console.log(error);
    // });
  }

  useEffect(() => {
        console.log('use effect');
        //getCollections();
        if(collections){
          console.log(collections);
        }
        if(wallet){
          console.log(wallet);
        }
        if(data){
          console.log(data);
        }
  });


  const Styles = styled.div`
  table {
    background-color: #192026;
    color:white;
    border-spacing: 0;
    position:relative;

    tr {
      td {
        border-bottom: 0;
      }
    }

    th{
      background-color: #192026;
      top:0;
      position:sticky;
    }

    th,
    td {
      margin: 0;
      padding: 0.2rem;
    }
  }
`

useEffect(() => {
  console.log('use effect');

  if(data){
    console.log(data);
  }

});

if(data){
  return (
    <Styles>
    <table>
      <thead>
        <tr>
          <th align="left">Name</th>
          <th align="left">FP</th>
          <th align="left">Ceiling</th>
          <th align="left">Volume</th>
          <th align="left">Total Sales</th>
          <th align="left">Average</th>
          <th align="left">Symbol</th>
        </tr>
      </thead>
      <tbody>
        {data.contracts.edges.map((collection: { node: { name: Key | null | undefined; stats: { floor: boolean | ReactChild | ReactFragment | ReactPortal | null | undefined; ceiling: boolean | ReactChild | ReactFragment | ReactPortal | null | undefined; volume: boolean | ReactChild | ReactFragment | ReactPortal | null | undefined; totalsales: boolean | ReactChild | ReactFragment | ReactPortal | null | undefined; average: boolean | ReactChild | ReactFragment | ReactPortal | null | undefined; }; symbol: boolean | ReactChild | ReactFragment | ReactPortal | null | undefined; }; }) => (
          <tr 
            key={collection.node.name}
          >
             <td style={{fontFamily: "Press Start 2P"}}>
              {collection.node.name} 
            </td>
            <td >
              {collection.node.stats.floor}
            </td>
            <td align="left">{collection.node.stats.ceiling}</td>
            <td align="left">{collection.node.stats.volume}</td>
            <td align="left">{collection.node.stats.totalsales}</td>
            <td align="left">{collection.node.stats.average}</td>
            <td style={{fontFamily: "Press Start 2P"}} align="left"><img className="symbolIcon" src={ethereumIcon}></img>{collection.node.symbol}</td>
          </tr>
        ))}
      </tbody>
    </table>
    </Styles>
  );
}else{
  return(
  <h1>Loading...</h1>
  );
}
};
