import React, { ReactNode } from "react";
import {Row, Col, Container} from "react-bootstrap";
import enlargeIcon from './enlargeIcon.png'
import solanaIcon from './solanaIcon.png'
import styled from 'styled-components'
import { Key, ReactChild, ReactFragment, ReactPortal, useCallback, useEffect, useMemo, useState } from 'react';
import axios from 'axios';
import { useWallet } from '@solana/wallet-adapter-react';
import { useQuery, gql } from "@apollo/client";
import {
  BrowserRouter as Router,
  Route,
  Routes,
  Link,
} from "react-router-dom";
import './Window.css';
import { forEach } from "lodash";

interface Props {
    children?: ReactNode
    // any props that come into the component
    wallet: any
}

export const CollectionsTable = ({ children, ...props }: Props) => {

  const [data, setData] = useState<any>();
  const wallet = props.wallet;

  const getCollections = async () => {

    var config = {
      method: 'get',
      url: 'https://api-mainnet.magiceden.dev/v2/launchpad/collections?offset=0&limit=20'
    };

    axios(config)
    .then(function (response) {
      //console.log(JSON.stringify(response.data));
      return response.data;
      }).then(function(res) {
        console.log('get stats');

        var newArray:any = [];
        res.forEach(function(collection:any){
          var getStats = {
            method: 'get',
            url: 'https://api-mainnet.magiceden.dev/v2/collections/' + collection.symbol + '/stats'
          };

          axios(getStats)
            .then(function (response) {
              let newObj = {
                "description":collection.description,
                "featured":collection.featured,
                "image":collection.image,
                "launchDatetime":collection.launchDatetime,
                "name":collection.name,
                "price":collection.price,
                "size":collection.size,
                "symbol":collection.symbol,
                "volume": response.data.volumeAll,
                "listed": response.data.listedCount
              };
              newArray.push(newObj);
            })
            .catch(function (error) {
              console.log(error);
            });
        });

      const Obj = Object.fromEntries(newArray);
        console.log(Obj);
        setData(Obj);  
        console.log('saved data');
        console.log(data);
      }).catch(function (error) {
      console.log(error);
    });
  }

  useEffect(() => {
        console.log('use effect');
        if(wallet){
          //console.log(wallet);
        }
  });


  const Styles = styled.div`
  div{

  }
  table {
    background-color: #192026;
    color:white;
    border-spacing: 0;
    position:relative;
    width:100%;
    overflow-y:scroll;
    overflow-x:hidden;

    tr {
      td {
        border-bottom: 0;
      }
    }

    th{
      background-color: #192026;
      top:0;
      position:sticky;
    }

    tr:last-child.td:last-child{
      border-radius: 15px;
    }

    th,
    td {
      margin: 0;
      padding: 0.2rem;
    }
  }
`

useEffect(() => {
  const intervalId = setInterval(() => {  //assign interval to a variable to clear it.
    console.log('use effect');
    getCollections();

    if(data){
      console.log('data');
      console.log(data);
    }

  }, 6000)

  return () => clearInterval(intervalId); //This is important

}, [useState])


return (
  <>
  {data ? (
    <Styles>
      <div>
    <table>
      <thead>
        <tr>
          <th align="left">Name</th>
          <th align="left">Launch Date</th>
          <th align="left">FP</th>
          <th align="left">Volume</th>
          <th align="left">Symbol</th>
        </tr>
      </thead>
      <tbody>
        { data.map( function(collection:any){

          <tr 
            key={collection.name}
          >
            <td style={{fontFamily: "Press Start 2P"}}>
            <Link to={`/Collection/${collection.symbol}`} key={collection.symbol} className="collectionLink">{collection.name}</Link>
            </td>
            <td align="left">{new Date(collection.launchDatetime).toDateString()}</td>
            <td align="left"><img className="symbolIcon" src={solanaIcon}></img>{collection.price}</td>
            <td align="left">{collection.volume}</td>
            <td style={{fontFamily: "Press Start 2P"}} align="left">{collection.symbol}</td>
          </tr>

        })

        }
      </tbody>
    </table>
    </div>
  </Styles> 
    ) : (
      <h1>Loading...</h1>
  )}
  </>
);
};
