import { useEffect, useState } from "react";
import styled from "styled-components";
import Countdown from "react-countdown";
import { Button, CircularProgress, Snackbar } from "@material-ui/core";
import Alert from "@material-ui/lab/Alert";
import './home.css';

import * as anchor from "@project-serum/anchor";

import { LAMPORTS_PER_SOL } from "@solana/web3.js";

import { useAnchorWallet } from "@solana/wallet-adapter-react";
import { WalletDialogButton } from "@solana/wallet-adapter-material-ui";

import {
  CandyMachine,
  awaitTransactionSignatureConfirmation,
  getCandyMachineState,
  mintOneToken,
  shortenAddress,
} from "./candy-machine";

const ConnectButton = styled(WalletDialogButton)``;

const CounterText = styled.span``; // add your styles here

const MintContainer = styled.div``; // add your styles here

const MintButton = styled(Button)``; // add your styles here

export interface HomeProps {
  candyMachineId: anchor.web3.PublicKey;
  config: anchor.web3.PublicKey;
  connection: anchor.web3.Connection;
  startDate: number;
  treasury: anchor.web3.PublicKey;
  txTimeout: number;
}

const Home = (props: HomeProps) => {
  const [balance, setBalance] = useState<number>();
  const [isActive, setIsActive] = useState(false); // true when countdown completes
  const [isSoldOut, setIsSoldOut] = useState(false); // true when items remaining is zero
  const [isMinting, setIsMinting] = useState(false); // true when user got to press MINT

  const [itemsAvailable, setItemsAvailable] = useState(0);
  const [itemsRedeemed, setItemsRedeemed] = useState(0);
  const [itemsRemaining, setItemsRemaining] = useState(0);

  const [alertState, setAlertState] = useState<AlertState>({
    open: false,
    message: "",
    severity: undefined,
  });


  const [startDate, setStartDate] = useState(new Date(props.startDate));

  const wallet = useAnchorWallet();
  const [candyMachine, setCandyMachine] = useState<CandyMachine>();

  const refreshCandyMachineState = () => {
    (async () => {
      if (!wallet) return;

      const {
        candyMachine,
        goLiveDate,
        itemsAvailable,
        itemsRemaining,
        itemsRedeemed,
      } = await getCandyMachineState(
        wallet as anchor.Wallet,
        props.candyMachineId,
        props.connection
      );

      setItemsAvailable(itemsAvailable);
      setItemsRemaining(itemsRemaining);
      setItemsRedeemed(itemsRedeemed);

      setIsSoldOut(itemsRemaining === 0);
      setStartDate(goLiveDate);
      setCandyMachine(candyMachine);
    })();
  };

  const onMint = async () => {
    try {
      setIsMinting(true);
      if (wallet && candyMachine?.program) {
        const mintTxId = await mintOneToken(
          candyMachine,
          props.config,
          wallet.publicKey,
          props.treasury
        );

        const status = await awaitTransactionSignatureConfirmation(
          mintTxId,
          props.txTimeout,
          props.connection,
          "singleGossip",
          false
        );

        if (!status?.err) {
          setAlertState({
            open: true,
            message: "Congratulations! Mint succeeded!",
            severity: "success",
          });
        } else {
          setAlertState({
            open: true,
            message: "Mint failed! Please try again!",
            severity: "error",
          });
        }
      }
    } catch (error: any) {
      // TODO: blech:
      let message = error.msg || "Minting failed! Please try again!";
      if (!error.msg) {
        if (error.message.indexOf("0x138")) {
        } else if (error.message.indexOf("0x137")) {
          message = `SOLD OUT!`;
        } else if (error.message.indexOf("0x135")) {
          message = `Insufficient funds to mint. Please fund your wallet.`;
        }
      } else {
        if (error.code === 311) {
          message = `SOLD OUT!`;
          setIsSoldOut(true);
        } else if (error.code === 312) {
          message = `Minting period hasn't started yet.`;
        }
      }

      setAlertState({
        open: true,
        message,
        severity: "error",
      });
    } finally {
      if (wallet) {
        const balance = await props.connection.getBalance(wallet.publicKey);
        setBalance(balance / LAMPORTS_PER_SOL);
      }
      setIsMinting(false);
      refreshCandyMachineState();
    }
  };

  useEffect(() => {
    (async () => {
      if (wallet) {
        const balance = await props.connection.getBalance(wallet.publicKey);
        setBalance(balance / LAMPORTS_PER_SOL);
      }
    })();
  }, [wallet, props.connection]);

  useEffect(refreshCandyMachineState, [
    wallet,
    props.candyMachineId,
    props.connection,
  ]);

  return (
    <main>

      {/* {wallet && <p>Balance: {(balance || 0).toLocaleString()} SOL</p>} */}
      <div className="city">
      {<div id="gif" className="video">
        <video loop src="gif.mov"width="75%" autoPlay></video>
      </div>}
      {/* <div className="mintContainer">
      {wallet && (
        <p id="walletP">Wallet: {(wallet.publicKey.toBase58() || "")}</p>
      )} 

      {wallet && <p>Total Available: {itemsAvailable}</p>}

      {wallet && <p>Redeemed: {itemsRedeemed}</p>}

      {wallet &&<p>Remaining: {itemsRemaining}</p>}     

      </div> */}

      <MintContainer>
        {!wallet ? (
          <div className="connectButton"><ConnectButton id="button">Connect Wallet</ConnectButton></div>
        ) : (
          <div id="mint">
            <MintButton id="mintButton"
            disabled//</div>={isSoldOut || isMinting || !isActive}
            onClick={onMint}
            variant="contained"
          >
            {isSoldOut ? (
              "SOLD OUT"
            ) : isActive ? (
              isMinting ? (
                <CircularProgress />
              ) : (
                "MINT"
              )
            ) : (
              <Countdown
                date={startDate}
                onMount={({ completed }) => completed && setIsActive(true)}
                onComplete={() => setIsActive(true)}
                renderer={renderCounter}
              />
            )}
          </MintButton>
          </div>
        )}
      </MintContainer>

      <Snackbar
        open={alertState.open}
        autoHideDuration={6000}
        onClose={() => setAlertState({ ...alertState, open: false })}
      >
        <Alert
          onClose={() => setAlertState({ ...alertState, open: false })}
          severity={alertState.severity}
        >
          {alertState.message}
        </Alert>
      </Snackbar>
</div>

<div className="videoContainer">
  <video id="commercial" src="commercial.mov" width="75%" controls></video> 
</div>

      {<div id="about"><div className="aboutContainer">
        <h1 className="h1">ABOUT</h1>
        <p>Rugz that are thugs... 
        </p><p>A collection of 6,969 uniquely generated collectible ThugRugz available for minting on the Solana blockchain.  
        </p><p>Other than just being a development team, we here at THUGRUGZ treasure the Solana ecosystem because we were traders before we were investors on this project. Solana changed my life, and seeing this downtrend in the ecosystem is so heartbreaking. This is because people just keep shitposting projects, and we aim to change EVERYTHING with this project.
        </p><p>How? Let me explain. When you mint a THUGRUG, not only are you getting a badass NFT, but you're also getting a lottery ticket to be entered in a drawing for .1-5 SOL! YES! Many of you will randomly receive small to large amounts of SOL just for minting. This is the first way we intend to give back to the community. 
        </p><p>Secondly, your THUGRUG grants you access to the THUGRUG DAO, here you guys can control what the holders percentage of royalties is spent on, be it retreats across the world or buying top Solana projects to fractionalize. THE CHOICE IS YOURS!
	      </p><p>Also, just by holding a THUGRUG you will be entered into weekly draws for airdrops(sol or nfts). You might even be entered in a draw for a TESLA or meeting up with your favorite influencers. WHO KNOWS WHAT CRAZY SHIT WE WILL DO?
	      </p><p>Just know the dev team is comprised of hardworking people who LOVE nfts and hope to bring excitement and life back into the Solana ecosystem. That’s the best thing any REAL project can do for a community.</p>
        </div></div>}

        {<div className="Featured Rugz">
          <h2>Featured Rugz</h2>
          <div className="row">
            <div className="column">
              <img src="wedding.jpg"></img>
              <img src="rocks.jpg"></img>
              <img src="falls2.jpg"></img>
              <img src="paris.jpg"></img>
            </div>
            <div className="column">
              <img src="underwater.jpg"></img>
              <img src="ocean.jpg"></img>
              <img src="wedding.jpg"></img>
              <img src="mountainskies.jpg"></img>
            </div>
          </div>
        </div>}

      {<div id="roadMapBg"><div id="roadmap" className="roadMapContainer"><h1 className="h1">ROADMAP</h1><h3>PHASE 1</h3><ul><li>THUG RUGZ GO LIVE: LAUNCH 6,969 RUGZ</li><ul><li>NO MAX PER WALLET</li><li>MINTERS WILL BE RANDOMLY SELECTED TO RECEIVE .1 - 5 SOL</li><li>YES, YOU HEARD RIGHT. YOU CAN WIN UP TO 5 SOL BY HITTING THAT MINT BUTTON</li></ul></ul><h3>PHASE 2</h3><ul><li>CREATION OF THE COMMUNITY FUND, 20% OF ROYALTIES, 100% CONTROLLED BY THE COMMUNITY</li><li>TESLA MODEL 3 GIVEAWAY</li><li>ROLEX GIVEAWAY</li></ul><h3>PHASE 3</h3><ul><li>50 LUCKY HOLDERS WILL BE CHOSEN TO RECEIVE 1 SOL</li><li>AFTER THE INITIAL 50, WE WILL BE CHOOSING 5 HOLDERS WEEKLY FOR 1 YEAR FOR VARYING AMOUNTS OF SOLANA AIR DROPS MAYBE 1 MAYBE 10 SOLS, MAYBE EVEN A HOT NFT WHO KNOWS?</li></ul><h3>PHASE 4</h3><ul><li>6.9% OF MINTING PROCEEDS WILL GO TO ST. JUDE CHILDREN'S HOSPITAL</li></ul><h3>PHASE 5</h3><ul><li>WEEKLY SNAPSHOTS FOR OUR UTILITY TOKEN $RUGZ</li></ul><h3>PHASE 6</h3><ul><li>OUR FINAL GOAL IS TO RANDOMLY SELECT 1 LUCKY HOLDER A WEEK THIS PERSON WILL HAVE THEIR RUG SPECIALLY CRAFTED FOR THEM. WE WILL DO THIS OVER THE COURSE OF A YEAR OR UNTIL 53 RUGZ ARE MAILED OUT.</li></ul></div></div>}
      {/* {wallet && <div id="rarity" className="rarityContainer"><h1 className="h1">RARITY</h1></div>} */}

    </main>
  );
};

interface AlertState {
  open: boolean;
  message: string;
  severity: "success" | "info" | "warning" | "error" | undefined;
}

const renderCounter = ({ days, hours, minutes, seconds, completed }: any) => {
  return (
    <CounterText>
      {hours + (days || 0) * 24} hours, {minutes} minutes, {seconds} seconds
    </CounterText>
  );
};

export default Home;
