import React, { ReactNode } from "react";
import {Row, Col, Container} from "react-bootstrap";
import enlargeIcon from './enlargeIcon.png'
import ethereumIcon from './ethereumIcon.png'
import styled from 'styled-components'
import { Key, ReactChild, ReactFragment, ReactPortal, useCallback, useEffect, useMemo, useState } from 'react';
import axios from 'axios';
import { useQuery, gql } from "@apollo/client";

interface Props {
    children?: ReactNode
    // any props that come into the component
    data?: any
}

//icy tools trending collections on eth query
const TRENDING_COLLECTIONS_QUERY = gql`
query TrendingCollections {
  contracts(orderBy: SALES, orderDirection: DESC) {
    edges {
      node {
        address
        ... on ERC721Contract {
          name
          stats {
            totalSales
            average
            ceiling
            floor
            volume
          }
          symbol
        }
      }
    }
  }
}
`;

export const CollectionsTable = ({ children, ...props }: Props) => {

  const { data, loading, error } = useQuery(TRENDING_COLLECTIONS_QUERY, {
    context: {
        headers: {
            "Content-Type": "application/json",
            "x-api-key": "02b0c4688eec42f99af574960ebf8c28"//process.env.REACT_APP_ICY_API_KEY
        }
    }
})

  const Styles = styled.div`
  table {
    background-color: #192026;
    color:white;
    border-spacing: 0;
    position:relative;

    tr {
      td {
        border-bottom: 0;
      }
    }

    th{
      background-color: #192026;
      top:0;
      position:sticky;
    }

    th,
    td {
      margin: 0;
      padding: 0.2rem;
    }
  }
`

  return (
    <Styles>
    <table>
      <thead>
        <tr>
          <th align="left">Name</th>
          <th align="left">FP</th>
          <th align="left">Ceiling</th>
          <th align="left">Volume</th>
          <th align="left">Total Sales</th>
          <th align="left">Average</th>
          <th align="left">Symbol</th>
        </tr>
      </thead>
      <tbody>
        {props.data.contracts.edges.map((collection: { node: { name: Key | null | undefined; stats: { floor: boolean | ReactChild | ReactFragment | ReactPortal | null | undefined; ceiling: boolean | ReactChild | ReactFragment | ReactPortal | null | undefined; volume: boolean | ReactChild | ReactFragment | ReactPortal | null | undefined; totalsales: boolean | ReactChild | ReactFragment | ReactPortal | null | undefined; average: boolean | ReactChild | ReactFragment | ReactPortal | null | undefined; }; symbol: boolean | ReactChild | ReactFragment | ReactPortal | null | undefined; }; }) => (
          <tr 
            key={collection.node.name}
          >
             <td style={{fontFamily: "Press Start 2P"}}>
              {collection.node.name} 
            </td>
            <td >
              {collection.node.stats.floor}
            </td>
            <td align="left">{collection.node.stats.ceiling}</td>
            <td align="left">{collection.node.stats.volume}</td>
            <td align="left">{collection.node.stats.totalsales}</td>
            <td align="left">{collection.node.stats.average}</td>
            <td style={{fontFamily: "Press Start 2P"}} align="left"><img className="symbolIcon" src={ethereumIcon}></img>{collection.node.symbol}</td>
          </tr>
        ))}
      </tbody>
    </table>
    </Styles>
  );
};
